INSERT IGNORE INTO `teamresources` (`teamID`, `teamName`, `teamDescription`, `pageLayout`) VALUES
	(1, 'Sound', 'Sound Team Resources', '<p align="center"><strong>Worship Ministry Directions <br /></strong><strong>Fall 2010 (revised 2013, 2016)</strong></p>\r\n<table style="width: 100%;">\r\n<tbody>\r\n<tr>\r\n<td style="width: 50%;">\r\n<p>&nbsp;</p>\r\n<p><strong>Cultivate spirit of humility </strong><em>(recognizing/acknowledging our need for God)</em></p>\r\n<ul>\r\n<li>Prayer a priority during Tuesday PM and Sunday AM rehearsals</li>\r\n</ul>\r\n<p><strong>&nbsp;</strong><strong>Worship more experiential<br /></strong></p>\r\n<ul>\r\n<li>Intentional inclusion of strength and soul components in worship <em>(heart-volitional, soul-emotional, mind-intellectual, and strength-physical)</em></li>\r\n<li>Variety in ways of reading/declaring Scripture</li>\r\n<li>More congregational participation and interaction</li>\r\n<li>Sharing God stories/testimonies</li>\r\n<li>Creative Worship Arts Team experiment</li>\r\n</ul>\r\n<p><strong>&nbsp;</strong><strong>Music trending continued contemporary</strong></p>\r\n<ul>\r\n<li>Connecting with our young adults and youth - &rdquo;youthizing&rdquo; our congregation</li>\r\n<li>15 new songs last year</li>\r\n<li>15+ new songs this year <em>(over 1 new song per month)</em></li>\r\n<li>Song suggestion polling from ministry members &ndash; via computer</li>\r\n</ul>\r\n<p><strong>&nbsp;</strong><strong>&nbsp;</strong><strong>Continued integration of 3 ethnic ministries</strong></p>\r\n<ul>\r\n<li>Joint services &ndash; Thanksgiving service, Christmas musical, Dec. 5-PM</li>\r\n<li>Cross-ethnic ministry in worship teams</li>\r\n</ul>\r\n<p><strong>&nbsp;</strong><strong>Continued inclusion of youth and children</strong></p>\r\n<ul>\r\n<li>Youth in worship singers and band</li>\r\n<li>Youth worship team leading periodically</li>\r\n<li>Scripture readings and pray-ers</li>\r\n<li>Special music/creative movement</li>\r\n<li>Kids segments on Sundays w/ no Kingdom Kidz</li>\r\n</ul>\r\n<p>&nbsp;<strong>Expectations of worship team ministry participants </strong></p>\r\n<ul>\r\n<li>Seeking God individually<em>&nbsp;</em></li>\r\n<li>Visit new song evaluation page on website each month</li>\r\n<li>Music preparation for rehearsals <em>(use of website resources)</em></li>\r\n<li>Punctual for rehearsals</li>\r\n<li>Keep availability current on Team Availability calendar <em>(website)</em></li>\r\n<li>Next ministry meeting -&nbsp; Sunday, January 9 <em>(after AM service)<br /></em></li>\r\n</ul>\r\n</td>\r\n<td style="width: 50%;">\r\n<p>This page contains links to resources meant to help the sound team with their service.</p>\r\n<ul>\r\n<li><span style="color: #000080; background-color: #00ff00;"><em><strong><a href="https://onedrive.live.com/redir?page=view&amp;resid=EE04239D514E4EFE!3396&amp;authkey=!AH7dvOJx1aoK-Dw"><span style="color: #000080; background-color: #00ff00;">OneNote Library of information</span></a></strong></em></span></li>\r\n<li>SCCC Sound Seminar 101: General sound team procedures</li>\r\n<li>Equipment Inventory and Use: Describes the sound equipment that SCCC has and usage information</li>\r\n<li>Microphone and Monitor Jacks: Descibes the stage location of the sound jacks</li>\r\n<li>Sound Board Settings Chart: Blank settings chart for the sound board</li>\r\n<li>Sound Flowchart/Checklist: Sound operations checklist</li>\r\n</ul>\r\n<p>&nbsp;</p>\r\n<p><span style="font-size: medium;"><strong>Behringer X32 Training</strong></span></p>\r\n<ul>\r\n<li><a href="https://www.youtube.com/results?search_query=behringer+x32+training&amp;oq=behringer+x32+training">All tutorials </a>- The best are by Drew Brashier from NRCC (North Ridge Community Church).&nbsp; They are generally quite short and cover specific topics.&nbsp; There are a lot of them, so scroll down and check several pages.</li>\r\n<li><a href="https://www.youtube.com/results?search_query=behringer+x32+webinar&amp;oq=behringer+x32+webin">The Behringer Webinars</a> - cover topics in depth, however they are quite long.&nbsp;</li>\r\n<li><a href="http://www.youtube.com/results?search_query=behringer+x32+sweetwater+training&amp;oq=behringer+x32+sweetwater+training">Overviews of the X32</a>&nbsp;- the Sweetwater videos are quite good</li>\r\n<li><a href="http://www.behringer.com/assets/X32_P0ASF_EQSG_EN.pdf">Quick start Manual</a> for the X32</li>\r\n</ul>\r\n<p>Finally, you should download the XEdit software for either the MAC or PC.&nbsp; It is an emulation of the front control panel of the X32 mixer and is used to control the mixer from a computer.&nbsp; You can press all of the software buttons, move the faders and generally get a good feel for the mixer.&nbsp; Of course since the mixer is not connected to the computer, no actual settings on the mixer will be affected.&nbsp;</p>\r\n<p>Here is the link:&nbsp;<a href="http://www.behringer.com/EN/Products/X32.aspx">http://www.behringer.com/EN/Products/X32.aspx</a>&nbsp;&nbsp; go into the downloads section to get XEdit</p>\r\n<p>&nbsp;</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>'),
	(2, 'Media', 'Media Team Resources', '<p align="center"><strong>Worship Ministry Directions <br /></strong><strong>Fall 2010</strong></p>\r\n<p>&nbsp;<strong>Cultivate spirit of humility </strong><em>(recognizing/acknowledging our need for God)</em></p>\r\n<ul>\r\n<li>Prayer a priority during Tuesday PM and Sunday AM rehearsals</li>\r\n</ul>\r\n<p><strong>&nbsp;</strong><strong>Worship more experiential<br /></strong></p>\r\n<ul>\r\n<li>Intentional inclusion of strength and soul components in worship <em>(heart-volitional, soul-emotional, mind-intellectual, and strength-physical)</em></li>\r\n<li>Variety in ways of reading/declaring Scripture</li>\r\n<li>More congregational participation and interaction</li>\r\n<li>Sharing God stories/testimonies</li>\r\n<li>Creative Worship Arts Team experiment</li>\r\n</ul>\r\n<p><strong>&nbsp;</strong><strong>Music trending continued contemporary</strong></p>\r\n<ul>\r\n<li>Connecting with our young adults and youth - &rdquo;youthizing&rdquo; our congregation </li>\r\n<li>15 new songs last year</li>\r\n<li>15+ new songs this year <em>(over 1 new song per month)</em></li>\r\n<li>Song suggestion polling from ministry members &ndash; via computer</li>\r\n</ul>\r\n<p><strong>&nbsp;</strong><strong>&nbsp;</strong><strong>Continued integration of 3 ethnic ministries</strong></p>\r\n<ul>\r\n<li>Joint services &ndash; Thanksgiving service, Christmas musical, Dec. 5-PM</li>\r\n<li>Cross-ethnic ministry in worship teams</li>\r\n</ul>\r\n<p><strong>&nbsp;</strong><strong>Continued inclusion of youth and children</strong></p>\r\n<ul>\r\n<li>Youth in worship singers and band</li>\r\n<li>Youth worship team leading periodically</li>\r\n<li>Scripture readings and pray-ers</li>\r\n<li>Special music/creative movement</li>\r\n<li>Kids segments on Sundays w/ no Kingdom Kidz</li>\r\n</ul>\r\n<p>&nbsp;<strong>Expectations of worship team ministry participants </strong></p>\r\n<ul>\r\n<li>Seeking God individually<em></em></li>\r\n<li>Visit new song evaluation page on website each month<em></em></li>\r\n<li>Music preparation for rehearsals <em>(use of website resources)</em></li>\r\n<li>Punctual for rehearsals<em></em></li>\r\n<li>Keep availability current on Team Availability calendar <em>(website)</em></li>\r\n<li>Next ministry meeting -&nbsp; Sunday, January 9 <em>(after AM service)<br /></em></li>\r\n</ul>\r\n<p align="center"><span style="color: #ff0000;"><strong><br />We are currently looking into issues with the user profile "media" on the Media Computer, please use "medialocal" (same password as before) until further notice</strong></span></p>\r\n<h2 style="text-align: center;"><span style="color: #ff0000;">New Media Computer &amp; Software</span></h2>\r\n<p>We are pleased to announce that a new media computer has been installed at the media desk. This new computer is a Mac Mini which is running ProPresenter instead of SundayPlus. We will still&nbsp;use Microsoft Powerpoint for displaying the sermon slides.</p>\r\n<p>Both the old PC and new Mac are currently setup at the media desk. The old PC will be connected until we have had a chance to perform training on the new system.</p>\r\n<p>If you have a Mac, you can download a fully functional trial copy of ProPresenter (<a href="http://www.renewedvision.com/pro_download.php" target="_blank">click here</a>) and explore it for yourself.</p>\r\n<p>If you have any questions or concerns, please send an email to <a href="mailto:worship@drawcloser.com">worship@drawcloser.com</a>.</p>\r\n<p>Please refer to the following information links to help transition your skills to the new environment:</p>\r\n<p><strong><span style="text-decoration: underline;">Mac Tutorials</span></strong></p>\r\n<ul>\r\n<li>General Information and Tutorials Page (<a style="color: #4ea5cd; font-weight: bold; text-decoration: none;" href="http://www.apple.com/findouthow/mac/" target="_blank">click here</a>)&nbsp;</li>\r\n<li>Switching From PC to Mac&rsquo; (<a style="color: #4ea5cd; font-weight: bold; text-decoration: none;" href="http://www.apple.com/findouthow/mac/#pctomac" target="_blank">click here</a>)</li>\r\n</ul>\r\n<p>&nbsp;</p>\r\n<p><strong><span style="text-decoration: underline;">ProPresenter 3 (current version)</span></strong></p>\r\n<ul>\r\n<li>Information Page (<a style="color: #4ea5cd; font-weight: bold; text-decoration: none;" href="http://www.renewedvision.com/pp3.php" target="_blank">click here</a>)</li>\r\n<li>User\'s Manual (<a style="color: #4ea5cd; font-weight: bold; text-decoration: none;" href="http://www.renewedvision.com/downloads/pp3_manual.pdf" target="_blank">click here</a>)</li>\r\n<li>Video Tutorials (<a href="http://www.renewedvision.com/pp3.php?page=tour" target="_blank">click here</a>)</li>\r\n</ul>\r\n<p>&nbsp;</p>\r\n<p><strong><span style="text-decoration: underline;">Upcoming release: ProPresenter 4</span></strong></p>\r\n<ul>\r\n<li>New Features and Information Page (<a href="http://www.renewedvision.com/pro4.php" target="_blank">click here</a>)</li>\r\n<li>User\'s Manual (<a href="http://www.renewedvision.com/downloads/pro_4_manual.pdf" target="_blank">click here</a>)</li>\r\n</ul>'),
	(3, 'Lighting', 'Stage Lighting Team Resources', '<p align="center"><strong>Worship Ministry Directions <br /></strong><strong>Fall 2010</strong></p>\r\n<p>&nbsp;<strong>Cultivate spirit of humility </strong><em>(recognizing/acknowledging our need for God)</em></p>\r\n<ul>\r\n<li>Prayer a priority during Tuesday PM and Sunday AM rehearsals</li>\r\n</ul>\r\n<p><strong>&nbsp;</strong><strong>Worship more experiential<br /></strong></p>\r\n<ul>\r\n<li>Intentional inclusion of strength and soul components in worship <em>(heart-volitional, soul-emotional, mind-intellectual, and strength-physical)</em></li>\r\n<li>Variety in ways of reading/declaring Scripture</li>\r\n<li>More congregational participation and interaction</li>\r\n<li>Sharing God stories/testimonies</li>\r\n<li>Creative Worship Arts Team experiment</li>\r\n</ul>\r\n<p><strong>&nbsp;</strong><strong>Music trending continued contemporary</strong></p>\r\n<ul>\r\n<li>Connecting with our young adults and youth - &rdquo;youthizing&rdquo; our congregation </li>\r\n<li>15 new songs last year</li>\r\n<li>15+ new songs this year <em>(over 1 new song per month)</em></li>\r\n<li>Song suggestion polling from ministry members &ndash; via computer</li>\r\n</ul>\r\n<p><strong>&nbsp;</strong><strong>&nbsp;</strong><strong>Continued integration of 3 ethnic ministries</strong></p>\r\n<ul>\r\n<li>Joint services &ndash; Thanksgiving service, Christmas musical, Dec. 5-PM</li>\r\n<li>Cross-ethnic ministry in worship teams</li>\r\n</ul>\r\n<p><strong>&nbsp;</strong><strong>Continued inclusion of youth and children</strong></p>\r\n<ul>\r\n<li>Youth in worship singers and band</li>\r\n<li>Youth worship team leading periodically</li>\r\n<li>Scripture readings and pray-ers</li>\r\n<li>Special music/creative movement</li>\r\n<li>Kids segments on Sundays w/ no Kingdom Kidz</li>\r\n</ul>\r\n<p>&nbsp;<strong>Expectations of worship team ministry participants </strong></p>\r\n<ul>\r\n<li>Seeking God individually<em></em></li>\r\n<li>Visit